mapper.select:
select typeSeq ,typeName from abType
where userSeq = ${userSeq}
and typeName LIKE CONCAT('%',#{shValue},'%')
order by ( CASE WHEN ASCII(SUBSTRING(typeName,1)) BETWEEN 48 AND 57 THEN 3 WHEN ASCII(SUBSTRING(typeName,1)) < 123 THEN 2 ELSE 1 END ), typeName;

mapper.update:
insert into abType(
       userSeq
      ,typeName
)value(
    ${userSeq}
    ,#{shValue}
)
on duplicate key update typeName = #{shValue}, userSeq = ${userSeq}
